* https://zenn.dev/uhyo/books/react-concurrent-handson-2/viewer/use-starttransition

```ts
import { Suspense } from './src/jsx/dom'
import { render } from './src/jsx/dom'
import { useState, startTransition, useTransition } from './src/jsx/hooks'

let sleeping: boolean = true
const Sleep1s = () => {
  if (sleeping) {
    throw new Promise<void>((resolve) =>
      setTimeout(() => {
        sleeping = false
        resolve()
      }, 1000)
    )
  }
  return <p>Hello!</p>
}

function SleepApp() {
  const [sleepIsShown, setSleepIsShown] = useState(false)
  return (
    <div>
      <Suspense fallback={<p>Loading...</p>}>{sleepIsShown ? <Sleep1s /> : null}</Suspense>
      <p>
        <button
          className='border p-1'
          onClick={() => {
            startTransition(() => {
              setSleepIsShown(true)
            })
          }}
        >
          Show Sleep1s
        </button>
      </p>
    </div>
  )
}

const HeavyContent = ({ count }) => {
  const startTime = performance.now()
  while (performance.now() - startTime < 1000) {}
  return <div>HeavyContent!!! {count}</div>
}

const Transition = () => {
  const [isPending, startTransition] = useTransition()
  const [count, setCount] = useState(0)
  const [showHeavy, setShowHeavy] = useState(false)
  return (
    <div>
      <button
        onClick={() => {
          startTransition(() => {
            setShowHeavy(true)
            setCount((c) => c + 1)
          })
        }}
      >
        {isPending ? 'Pending...' : 'Click me'}
      </button>
      {showHeavy && (
        <button
          onClick={() => {
            setShowHeavy(false)
          }}
          style="margin-left: 10px;"
        >
          Hide HeavyContent
        </button>
      )}
      <div>{showHeavy ? isPending ? '...' : <HeavyContent count={count} /> : count}</div>
    </div>
  )
}

export const App = () => {
  return (
    <div>
      <div>
        <div>
          <h3>startTransition with Suspense</h3>
          <SleepApp />

          <h3>useTransition</h3>
          <Transition />
        </div>
      </div>
    </div>
  )
}
```

