```ts
import { Style, css } from './src/helper/css'
import { render, Suspense } from './src/jsx/dom'
import { useState, startViewTransition, use } from './src/jsx/hooks'

const imagePromise = new Map<string, Promise<string>>()
const loadImage = (src: string) => {
  if (!imagePromise.has(src)) {
    imagePromise.set(
      src,
      new Promise((resolve) => {
        ;(async () => {
          await new Promise((resolve) => setTimeout(resolve, 1000))
          const image = new window.Image()
          image.onload = () => resolve(src)
          image.src = src
        })()
      })
    )
  }
  return imagePromise.get(src) as Promise<string>
}

const ThumbnailImage = () => <img src='https://avatars.githubusercontent.com/u/98495527' />
const OriginalImage = () => {
  const image = use(
    loadImage('https://github.com/honojs/hono/blob/main/docs/images/hono-logo.png?raw=true')
  )
  return (
    <div
      class={css`
        background: url('${image}');
        background-size: contain;
        background-repeat: no-repeat;
        background-position: center;
        width: 600px;
        height: 600px;
      `}
    />
  )
}

const Image = () => {
  const [showOriginalImage, setShowOriginalImage] = useState(false)
  return (
    <div
      onClick={() => startViewTransition(() => setShowOriginalImage(!showOriginalImage))}
      class={css`
        img,
        div {
          view-transition-name: image;
        }
      `}
    >
      {showOriginalImage ? (
        <Suspense
          fallback={
            <div
              class={css`
                width: 600px;
                height: 600px;
                display: flex;
                justify-content: center;
                align-items: center;
                background-image: url('https://avatars.githubusercontent.com/u/98495527');
                background-size: contain;
                background-repeat: no-repeat;
                background-position: center;
                background-color: rgba(255, 255, 255, 0.5);
                background-blend-mode: lighten;
              `}
            >
              Loading...
            </div>
          }
        >
          <OriginalImage />
        </Suspense>
      ) : (
        <ThumbnailImage />
      )}
    </div>
  )
}

export const App = () => {
  return (
    <>
      <Style />
      <div>
        <div>
          <div>
            <Image />
          </div>
        </div>
      </div>
    </>
  )
}

render(<App />, document.getElementById('root'))
