import { render, Suspense } from './src/jsx/dom'
import { Style, css, keyframes, viewTransition } from './src/jsx/dom/css'
import { useState, useViewTransition, use } from './src/jsx/hooks'

const imagePromise = new Map<string, Promise<string>>()
const loadImage = (src: string) => {
  if (!imagePromise.has(src)) {
    imagePromise.set(
      src,
      new Promise((resolve) => {
        ;(async () => {
          await new Promise((resolve) => setTimeout(resolve, 700))
          const image = new window.Image()
          image.onload = () => resolve(src)
          image.src = src
        })()
      })
    )
  }
  return imagePromise.get(src) as Promise<string>
}

const ThumbnailImage = ({ transition }: { transition: string }) => (
  <img src='https://avatars.githubusercontent.com/u/98495527' class={transition} />
)
const OriginalImage = ({
  transition,
  setIsImageShown,
}: {
  transition: string
  setIsImageShown: (boolean) => void
}) => {
  const [isUpdating] = useViewTransition()
  const image = use(
    loadImage('https://github.com/honojs/hono/blob/main/docs/images/hono-logo.png?raw=true')
  )
  if (!isUpdating) {
    setIsImageShown(true)
  }
  return (
    <div
      class={css`
        ${transition}
        background: url('${image}');
        background-size: contain;
        background-repeat: no-repeat;
        background-position: center;
        width: 600px;
        height: 600px;
      `}
    />
  )
}

const Image = () => {
  const [showOriginalImage, setShowOriginalImage] = useState(false)
  const [isShown, setIsShown] = useState(false)
  const [isImageShown, setIsImageShown] = useState(false)
  const [isUpdating, startViewTransition] = useViewTransition()


  const rotate360 = keyframes`
0% {
  transform: rotate(0deg);
}
100% {
  transform: rotate(360deg);
}
`
  const rotate20 = keyframes`
0% {
  transform: rotate(0deg);
}
50% {
  transform: rotate(20deg);
}
100% {
  transform: rotate(0deg);
}
`
  const fadeIn = keyframes`
0% {
  opacity: 0;
}
100% {
  opacity: 1;
}
`
  const fadeOut = keyframes`
0% {
  opacity: 1;
}
100% {
  opacity: 0;
}
`
  const loadingTransition = viewTransition(css`
    ::view-transition-old() {
      animation: ${rotate360} 0.5s ease-in-out;
    }

    ::view-transition-new() {
      animation: ${rotate360} 0.5s ease-in-out;
    }
  `)
  const loadedTransition = viewTransition(css`
    ::view-transition-old() {
      animation: ${fadeOut} 0.5s linear;
    }

    ::view-transition-new() {
      animation: ${fadeIn} 0.5s linear;
    }
  `)
  const switchTransition = viewTransition(css`
    ::view-transition-old() {
      animation: ${rotate20} 0.5s cubic-bezier(0.175, 0.885, 0.32, 1.275);
    }

    ::view-transition-new() {
      animation: ${rotate20} 0.5s cubic-bezier(0.175, 0.885, 0.32, 1.275);
    }
  `)

  return (
    <div
      onClick={() =>
        startViewTransition(() => {
          setShowOriginalImage(!showOriginalImage)
          setIsShown(true)
        })
      }
    >
      {showOriginalImage ? (
        <Suspense
          fallback={
            <div
              class={css`
                ${isUpdating ? loadingTransition : loadedTransition}
                width: 600px;
                height: 600px;
                display: flex;
                justify-content: center;
                align-items: center;
                background-image: url('https://avatars.githubusercontent.com/u/98495527');
                background-size: contain;
                background-repeat: no-repeat;
                background-position: center;
                background-color: rgba(255, 255, 255, 0.5);
                background-blend-mode: lighten;
              `}
            >
              Loading...
            </div>
          }
        >
          <OriginalImage
            transition={
              isImageShown ? switchTransition : isUpdating ? loadingTransition : loadedTransition
            }
            setIsImageShown={setIsImageShown}
          />
        </Suspense>
      ) : (
        <ThumbnailImage transition={isShown ? switchTransition : loadingTransition} />
      )}
    </div>
  )
}

export const App = () => {
  return (
    <>
      <Style />
      <div>
        <div>
          <div>
            <Image />
          </div>
        </div>
      </div>
    </>
  )
}

render(<App />, document.getElementById('root'))
```

